import React from 'react';
import { Box, Flex, Heading, Button, useColormode, useColorModeValue} from '@chakra-ui/react';
import { SunIcon, MoonIcon } from '@chakra-ui/icons';

const Header = () => {
  const {colorMode, toggleColorMode } = useColormode();
  const bg = useColormodeValue('teal.500', 'teal.700');
  const color = useColormodeValue('white', 'white');
  return (
      <Box bg={bg} px={4}>
        <Flex h={16} alignItems={'center'} justifyContent={'space-between'}>
          <Heading color={color} size='md'>
              Level 2 Website
          </Heading>
          <Flex alignItems={'center'}>
              <Button onClick={toggleColorMode} colorScheme='teal' variant='ghost'>
                  {colorMode === 'light' ? <MoonIcon/> : <SunIcon />}
              </Button>
          </Flex>
        </Flex>
      </Box>
    );   
  };

  export default Header;
